import { Component } from '@angular/core';

@Component({
  selector: 'app-page2',
  templateUrl: './page2.component.html',
  styleUrls: ['./page2.component.css']
})
export class Page2Component {

}
<!DOCTYPE html>
<html>
<head>
<style>
    body {
 background-color: black;
 background-image: url('your-image-url-here');
 background-size: 100% 177.78%;
 background-repeat: no-repeat;
 color: white;
 }
 .H1{
  color: black;
  text-align: center;
 }
 .container {
 display: flex;
 flex-direction: column;
 align-items: center;
 }
.grid-container {
  display: grid;
  grid-template-columns: auto;
  padding: 10px;
}
.button_menu{
    background-color: blue;
    border-radius: 8px;
    display: grid;
      grid-template-columns: auto auto auto auto;
      padding: 10px;
}
.button_m {
    display: flex;
    flex-direction: column;
    align-items: center;
    background-color: #007bff;
    color: #ffffff;
    border: none;
    padding: 10px;
    border-radius: 5px;
    cursor: pointer;
  }

  .button img {
    margin-bottom: 5px;
  }
  .span{
  font-size: 14px;
}


</style>
</head>
<body>


<div class="grid-container">
  <div ></div>

  <div class="grid-item">3</div>  
  <div class="grid-item">4</div>
  <div class="grid-item">5</div>
  <div class="grid-item">6</div>  
  <div class="grid-item">7</div>
  <div class="grid-item">8</div>
  <div class="grid-item">9</div> 
</div>
<div class="button_menu">
    <button routerLink="/page3" class="button">
        <img src="assets/icons/home.svg" alt="Icon" width="24" height="24">
        <span>Label</span>
      </button>
    <button routerLink="/page2">Page 2</button>
    <button routerLink="/page3">Page 3</button>
    <button routerLink="/page2">Page 4</button>
    <button routerLink="/page3">Page 4</button>
</div>
</body>
</html>
